response = requests.get(url)
soup = BeautifulSoup(response.text, 'html.parser')
propiedades = []
for row in soup.select('table.propiedades tr'):
    propiedades.append({
        'direccion': row.select_one('.direccion').text,
        'folio_real': row.select_one('.folio').text
    })
return propiedades
